con:config.script:
import groovy.util.XmlSlurper

def responseContent = testRunner.testCase.getTestStepByName("GetUKLocationByPostCode").getPropertyValue("response")

log.info(responseContent)

def xmlResponse=new XmlSlurper().parseText(responseContent)

log.info(xmlResponse)

def slurperResponse=new XmlSlurper().parseText(xmlResponse.Body.GetUKLocationByPostCodeResponse.GetUKLocationByPostCodeResult.toString())

// Find town called Clifton
for(int i = 0; i < slurperResponse.Table.size(); i++)
 {
 	if (slurperResponse.Table.Town[i].text() == "Clifton")
 	{
 		def ukTownName = slurperResponse.Table.Town[i].toString()
 		log.info(ukTownName)

 		testRunner.testCase.testSuite.project.setPropertyValue("UKTown", ukTownName) 		
 	}
 }

import groovy.util.XmlSlurper

def responseContent = testRunner.testCase.getTestStepByName("GetUKLocationByTown").getPropertyValue("response")

log.info(responseContent)

def xmlResponse=new XmlSlurper().parseText(responseContent)

log.info(xmlResponse)

def slurperResponse=new XmlSlurper().parseText(xmlResponse.Body.GetUKLocationByTownResponse.GetUKLocationByTownResult.toString())

// Find town = Clifton, county = Bedfordshire and postcode = SG17
for(int i = 0; i < slurperResponse.Table.size(); i++)
 {
 	if ((slurperResponse.Table.Town[i].text() == "Clifton") && (slurperResponse.Table.County[i].text() == "Bedfordshire") && (slurperResponse.Table.PostCode[i].text() == "SG17"))
 	{
 		def ukTownName = slurperResponse.Table.Town[i].toString()
 		log.info(ukTownName)

 		def ukCountyName = slurperResponse.Table.County[i].toString()
 		log.info(ukCountyName)

 		def ukPostCode = slurperResponse.Table.PostCode[i].toString()
 		log.info(ukPostCode)

		com.eviware.soapui.SoapUI.globalProperties.setPropertyValue("UKTown", ukTownName.toString())
		com.eviware.soapui.SoapUI.globalProperties.setPropertyValue("UKCounty", ukCountyName.toString())
		com.eviware.soapui.SoapUI.globalProperties.setPropertyValue("UKPostCode", ukPostCode.toString())
 	}
 }
